nta.declaration:
///Constants

const int NUM_PODS = 3;
const int N_STATION = 2;
const int STATION_VARIANCE = 1;
const int STATION_MEAN = 2;
const int POS_POD[NUM_PODS]={0,1,2};
const int previousStations[N_STATION][N_STATION] = {{0,1},{1,0}};
const int SWITCH_POS=5;
const int N_BRANCH = 3;
const int LENGTH_BRANCH[N_BRANCH]={13,6,6};
const int PODS_BRANCH[NUM_PODS]={0,0,0};
const bool SWITCH = true; //TODO: va bene così?Leggendo le specifiche si, ma dobbiamo decidere assieme se farlo cambiare o meno
/*** CLOCK ***/
clock beltClock;

//Here we are defining the pods with the auto-instance of Uppaal
typedef int[0,NUM_PODS-1] pod_t;
typedef int[0,N_STATION-1] stat_t;
typedef int [0,N_STATION-1] queue_t;
typedef int [0,N_STATION-1] firstSensor_t;
typedef int [0,N_STATION-1] secondSensor_t;
typedef int [0,N_STATION-1] dummySensor_t;

/*** GLOBAL VARIABLES***/
int MAX_POSITION = 0;
int pos_Id_Station[NUM_PODS];
int pods_position[NUM_PODS];
int pods_branch[NUM_PODS]; 
bool pods_blocked[NUM_PODS];
bool stations_blocked[N_STATION];
int station_position[N_STATION]={8,11};
int station_branch[N_STATION]={1,2};
bool station_free[N_STATION]={true,true};
bool valSensors[N_STATION][2];
int posSensor1[N_STATION] = {7,10};
int posSensor2[N_STATION] = {6,9};

//Tiene traccia del binomio pods_station quando la pods entra nella stazione
typedef struct{
  int idStation;
  int idPod;
  bool exit1;
} Station_Pod;

Station_Pod station_pod[N_STATION];

/*** CHANNELS ***/
broadcast chan initializer;
broadcast chan check;
broadcast chan move;
broadcast chan entryStation[N_STATION];
broadcast chan exitStation[N_STATION];
chan StopStations[N_STATION];

chan priority check,move < default < entryStation[N_STATION-1],exitStation[N_STATION-1];

/*** GLOBAL UTILITY FUNCTIONS ************************************************/


void initialize(){
    int i;
    for(i=0;i<NUM_PODS;i++){
        pods_position[i]=POS_POD[i];
        pods_branch[i] = PODS_BRANCH[i];
    }

    for(i=0;i<N_STATION;i++){
       station_pod[i].idStation = i;
       station_pod[i].idPod=-1;
    }
    for(i=0;i<N_STATION;i++){
       station_pod[i].exit1 = false;
    }
    for(i=0;i<N_BRANCH;i++){
        MAX_POSITION = MAX_POSITION + LENGTH_BRANCH[i];
    }
}

// aggiorna la posizione aggiungendo 1, chiamato dalla pods quando avanza 
void updateCurrentPositionGlobal(int id){
         if(pods_position[id]!=MAX_POSITION-1) pods_position[id]=pods_position[id] + 1;
         else pods_position[id]=0;
}

int getIdPodInPos(int pos, int branch){
    int i;
    for(i=0; i<NUM_PODS; i++){
        if(pods_position[i]==pos && pods_branch[i]==branch) return i;
    }
    return -1;
}

void checkBlockedGlobal(){
    int i,j;
    for(i=0; i<NUM_PODS; i++){
        pods_blocked[i]=false;
    }
    for(j=0; j<N_STATION; j++){
        for(i=0; i<NUM_PODS; i++){
            if(pods_position[i]+1 == posSensor1[j] && pods_branch[i] == station_branch[j]  && getIdPodInPos(pods_position[i]+1, pods_branch[i]) != -1 )  pods_blocked[i]=true;
        }
    }
}

//quando la pods entra nella stazione viene aggiornata la struttura dati utilizzata per tenere traccia delle pods nelle stazioni7
//TODO: ma se la station sta subito dopo lo switch?
void enter_station(int id){ 
    int i;
    for(i=0;i<N_STATION;i++){
            if(station_position[i]==pods_position[id]+1 && station_branch[i]==pods_branch[id]|| pods_position[id] == MAX_POSITION && station_position[i]==0 && station_branch[i]==pods_branch[id])
                station_pod[i].idPod=id;
    }
    checkBlockedGlobal();
}

//chiamata dalla pods, controlla se la stazione in cui è la pods è libera, se è così ritorna true altrimenti ritorna false
bool checkExitStation(int id){
    int i;
    for(i=0;i<N_STATION;i++){
        if(station_pod[i].idPod==id && station_free[station_pod[i].idStation]==true && station_pod[i].exit1==true ) { 
           return true;
        }
    }
    return false;
}
void resetExit(int id){
    int i;
    for(i=0;i<N_STATION;i++){
        if(station_pod[i].idPod==id){
           station_pod[i].exit1=false;
           station_pod[i].idPod=-1;
        }
     }
}

bool checkWaitingGlobal(int id){
    int i = 0;
    for (i = 0; i < N_STATION; i++ ){
        if(pods_position[id]==posSensor1[i] && station_branch[i]==pods_branch[id])
            return true;
    }
    return false;
}

bool checkIfStationGlobal(int id){
    bool result=false;
    int i;
    for(i=0; i<N_STATION; i++){
        if(pods_position[id]==station_position[i]) result = true;
    }
    return result;
}


void setValSensors(int index, int pos, bool value){
    valSensors[index][pos-1]=value;
}

bool getValSensors(int index, int pos){
    return valSensors[index][pos-1];
}

bool checkPodInPos(int pos,int id_sensor){
    int i;
    for(i=0; i<NUM_PODS; i++){
        if(id_sensor!=-1){
            if(pods_position[i]==pos && pods_branch[i]==station_branch[id_sensor]) return true;
        }else{
             if(pods_position[i]==pos)return true;
        }
    }
    return false;
}

bool checkPodBlockedInPos(int pos, int branch){
    int i;
    for(i=0; i<NUM_PODS; i++){
        if(pods_position[i]==pos && pods_branch[i] == branch && pods_blocked[i] == true ) return true;
    }
    return false;
}

bool checkPosIsSensor1(int pos){
    int i;
    for(i=0; i<N_STATION; i++){
        if(posSensor1[i]==pos) return true;
    }
    return false;
}

bool checkPodBlockedInSensor2(int id) {
    int i;
    for(i=0; i<NUM_PODS; i++){
        if(pods_position[i]==posSensor2[id] && checkPodBlockedInPos(pods_position[i], pods_branch[i])) return true;
    }
    return false;
}

int getIdStationQueue(int pos) {
    int i;
    for(i=0; i<N_STATION; i++){
        if(posSensor1[i]==pos) return i;
    }
    return -1;
}

void stopStationsGlobal(int id){
    int i;
    for(i=0; i<N_STATION; i++){
        if(previousStations[id][i]==1) stations_blocked[i] = true;
    }
}

void resumeStationsGlobal(int id){
    int i;
    for(i=0; i<N_STATION; i++){
        if(previousStations[id][i]==1) stations_blocked[i] = false;
    }
}
bool otherBranchNotEndedGlobal(int branch){
    int i;
    bool result = true;
    if(branch == 1){
        for(i=0; i<NUM_PODS; i++){
            if(pods_branch[i] == 2 && pods_position[i]-SWITCH_POS == LENGTH_BRANCH[2])result = false;
        }
    }
    return result;
}
bool checkSamePositionGlobal(int pod_id){
    bool result = false;
    int i;
    for(i=0; i<NUM_PODS; i++){
        if(pods_position[pod_id] != MAX_POSITION - 1){
            if(i!=pod_id && pods_position[pod_id]+1 == pods_position[i] && pods_branch[pod_id] == pods_branch[i]) result = true;
        }else{
            if(pods_position[i]==0) result = true;
        }
    }
    return result;
}

template.declaration:
//true because we suppose you are ready and set to false if check go wrong
bool ready = true;
bool waiting = false;
bool station = false;
int idStation = -1;

int curr_branch = PODS_BRANCH[id];
int current_pos=  POS_POD[id];



bool isReady(){
    return ready;
}

bool isStation(){
    return station;
}

bool isBlocked(){
    return pods_blocked[id];
}

int retrieve_station(int id){
    int c=-1;
    int i=0;
    for(i=0; i < N_STATION; i++){
        if(station_pod[i].idPod==id){
            c=i;
        }
    }
    return c;
}

void setReadyFalse(){
    ready=false;
}

int retrieve_waiting_station(int pos) {
    int c=-1;
    int i=0;
    for (i = 0; i < N_STATION; i++ ){
        if(pos==posSensor1[i])
            c = i;
    }
    return c;
}

void checkWaiting(){
    if(checkWaitingGlobal(id)) {
        waiting=true;
        idStation = retrieve_waiting_station(pods_position[id]);
    }
    else {
        waiting=false;
    }
}

void checkBlocked(){
    if((checkSamePositionGlobal(id) && checkPodBlockedInPos(pods_position[id]+1, pods_branch[id]))||(pods_position[id] == MAX_POSITION-1 && checkPodInPos(0,-1) && checkPodBlockedInPos(0,0)) ||(pods_position[id] == MAX_POSITION-1 && checkPosIsSensor1(0) && checkPodInPos(0,-1) && station_free[getIdStationQueue(0)]==false)  || checkPosIsSensor1(pods_position[id]+1) && checkPodInPos(pods_position[id]+1,-1) &&  station_free[getIdStationQueue(pods_position[id]+1)]==false) pods_blocked[id] = true;
    else pods_blocked[id] = false;
}

bool isWaiting(){
    return waiting;
}



void checkStation(){
      if(checkIfStationGlobal(id)){
        station=true;
        ready=false;
    }
    else{
        station=false;
        ready=true;
    }
}

void updateCurrentPosition(){
    checkBlocked();
    if(!pods_blocked[id]){
// controlla se si è alla fine del branch e se sull'altro branch non arriva un altro pod allo stesso momento
        if(curr_branch != 0 && current_pos - SWITCH_POS == LENGTH_BRANCH[curr_branch] && otherBranchNotEndedGlobal(curr_branch)){ //TODO: calcoli giusti? otherBranchNotEnded lo implementiamo in checkBlocked?
            current_pos = current_pos + 1;
            curr_branch=0;
            updateCurrentPositionGlobal(id);
            checkBlockedGlobal();
        // se non sei alla fine della branch=0 incrementa posizione (caso base)
        }else if(current_pos != MAX_POSITION-1){
            current_pos= current_pos + 1;
            updateCurrentPositionGlobal(id);
            checkBlockedGlobal();
            //checkStation();
// sei alla max position e ricominci il loop
        }else{
            current_pos=0;
            updateCurrentPositionGlobal(id);
            checkBlockedGlobal();
            //checkStation();
        }
//qui controlla se è su uno switch ( la pod numero 2 va nel secondo branch grazie a id!=2)
        if(SWITCH_POS == current_pos-1 && SWITCH && id!=2){
            curr_branch=1;
        }else if(SWITCH_POS == current_pos-1 && !SWITCH ||SWITCH_POS == current_pos-1 && SWITCH && id==2){
            curr_branch=2;
        }
        pods_branch[id]=curr_branch;
    }
    checkWaiting();
}

void freeBlocked(){
    pods_blocked[id] = false;
}

void initilaize(){
    checkWaiting();
    checkStation();
    checkBlocked();
}

void setStationPosition(){
    pods_position[id] = station_position[idStation];
    current_pos = station_position[idStation];
    station_pod[idStation].idPod = id;
}

